import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */
import BlogMeta from "../../components/BlogMeta";
import { NextSeo } from 'next-seo';
export const meta = {
  title: ["Update about 404 errors with now.json file"],
  tags: ["next"],
  layout: "post",
  publishDate: "2020-07-21",
  modifiedDate: false,
  seoDescription: "Explanation of how to solve the 404 error when refreshing the Next.js page",
  exclude: false,
  hideProgressBar: false
};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  meta
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <NextSeo title="Update about 404 errors with now.json file | Wajari.Dev" description="Explanation of how to solve the 404 error when refreshing the Next.js page" canonical="https://wajari.dev/blog/now-cleanurls" mdxType="NextSeo" />
    <BlogMeta data={props} mdxType="BlogMeta" />
    <h2>{`I have 404 errors on my blog, but`}</h2>
    <p>{`I was excited to launch my blog on next.js, I managed to solve almost the whole process and I was stuck in nonsense for too long.`}</p>
    <p>{`I was reading on forums and in the documentation and other people had 404 failures when refreshing the page, but it only happens in development environment,
not local.`}</p>
    <p>{`These two issues described well what was happening to me:`}</p>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/vercel/vercel/issues/3294"
        }}>{`404 errors with dynamic routes`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://github.com/vercel/next-site/issues/192"
        }}>{`Refresh page & 404 bug`}</a></li>
    </ul>
    <p>{`Thanks to `}<a parentName="p" {...{
        "href": "https://es.linkedin.com/in/mancontr/es-es"
      }}>{`Anxo`}</a>{`, my React teacher, He detected the bug. It's as simple as I didn't put the cleanUrls parameter
in the now.json file:`}</p>
    <pre><code parentName="pre" {...{}}>{`"cleanUrls": true
`}</code></pre>
    <p>{`Unbelievable! `}</p>
    <p>{`I hope it helps you if the same happens to you.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
